import React, { useState, useEffect } from 'react'
import axios from 'axios'

function Read() {
    const [data, setData] = useState(null)
    const { id } = useParams()

    useEffect(() => {
        axios.get(`/student/${id}`)
        .then((res) => {
            console.log(res.data)
            setData(res.data)
        })
        .catch((err) => console.log(err))
    }, [id])

    // Tambahkan pengecekan untuk data
    if (!data) {
        return <p>Loading...</p> // Menampilkan pesan loading saat data belum ada
    }

    return (
        <table className='table'>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>E-mail</th>
                    <th>Age</th>
                    <th>Gender</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{data.name}</td> {/* Asumsi data adalah objek tunggal */}
                    <td>{data.email}</td>
                    <td>{data.age}</td>
                    <td>{data.gender}</td>
                </tr>
            </tbody>
        </table>
    )
}

export default Read
