import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

function Home() {
    const [data, setData] = useState([])
    const [deleted, setDeleted] = useState(true)

    useEffect(()=>{
        if(deleted){
            setDeleted(false)
        }
        axios.get("/students")
        .then((res)=>{
            console.log(res.data)
            setData(res.data)
        })
        .catch((err)=>console.log(err))
    }, [deleted])

    function handleDelete(id){
        console.log(id)
        // axios.delete(`/delete_student/${id}`)
        // .then((res) => {
        //     setDeleted(true)
        // })
        // .catch((err) => {
        //     console.log(err)
        // })
    }
    return (
        <table className="table table-striped">
            <thead>
                <tr>
                    <th>No.</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Age</th>
                    <th>Gender</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {
                    data.map((student, index) => {
                        return (
                            <tr key={ student.id }>
                                <td>{ index + 1 }</td>
                                <td>{ student.name }</td>
                                <td>{ student.email }</td>
                                <td>{ student.age }</td>
                                <td>{ student.gender }</td>
                                <td>
                                    <Link to={`/read/${student.id}`} className='btn btn-info btn-md mx-1'>Detail</Link>
                                    <Link to={`/edit/${student.id}`} className='btn btn-warning btn-md mx-1'>Edit</Link>
                                    <button className='btn btn-danger btn-md mx-1' onClick={(e)=> handleDelete(student.id) }>Delete</button>
                                </td>
                            </tr>
                        )
                    }
                )}
            </tbody>
        </table>
    )
}

export default Home
